import React, {useEffect} from 'react';
import PropTypes from 'prop-types';
import {Task} from '../../models/task.class'
//importamos los estilos task.scss
import '../../styles/task.scss'
const TaskComponent = ({task}) => {

    useEffect(() => {
        console.log(`Create`, task);
        return () => {
            console.log(`Task: ${task.name} is going to unmount`);
        };
    }, [task]);

    return (
        <tr>
            <th className='fw-bold'>
                <span className='ms-2'>{task.name}</span>
            </th>
            <td className='align-middle'>
                <span>{task.description}</span>
            </td>
            <td className='align-middle'>
                {/**sustituir por baget*/}
                <span>{task.level}</span>
            </td>
            <td className='align-middle'>
                {/**sustituir por icons*/}
                {task.completed? (<i className='bi bi-toggle-on'></i>) : (<i className='bi-toggle-off' style={{color: 'grey'}}></i>)}
                <span>{task.completed? 'Completed' : 'Pending'}</span>	
            </td>

        </tr>

        /**<div>
            <h2 className='task-name'>Nombre: {task.name}</h2>
            <h3>Descripcion: {task.description}</h3>
            <h4>Nivel: {task.level}</h4>
            <h5>this task is: {task.completed ? 'Completo' : 'Pendiente'}</h5>
        </div>***/
    );
};

TaskComponent.propTypes ={
    task: PropTypes.instanceOf(Task),
}

export default TaskComponent;
