    const [access, setAccess] = useState(true);

    const updateAccess =()=>{
        setAccess(!access);
    }

    const loginAction = ()=>{
        setAccess(true);
    }

    const logoutAction = ()=>{
        setAccess(false);
    }


    let optionalButton;

    //if (access) {
    //    optionalButton = <button onClick={updateAccess}>Logout</button>
    //}else{
    //    optionalButton = <button onClick={updateAccess}>Login</button>
    //}
    





    return (
        <div>
            {optionalButton}
        </div>
    );
}

export default Optionalrender;
