import React, {useState} from 'react';

//Login/Logout buttons
const LoginButton=({loginAction}) => {
    return(
        <button onClick={loginAction}>Login</button>        
    )
}

const LogoutButton=({logoutAction}) => {
    return(
        <button onClick={logoutAction}>Logout</button>        
    )
}

// ? (Expresion true) && expression => se renderiza la expresion
// ? (Expresion false) && expression => no se renderiza la expresion


const Optionalrender = () => {
    
    const [access, setAccess] = useState(false);
    const [nmessage, setNmessage] = useState(0);

    //const updateAccess =()=>{
    //    setAccess(!access);
    //}

    const loginAction = ()=>{
        setAccess(true);
    }

    const logoutAction = ()=>{
        setAccess(false);
    }

    let optionalButton;
    //if (access) {
    //    optionalButton = <button onClick={updateAccess}>Logout</button>
    //}else{
    //    optionalButton = <button onClick={updateAccess}>Login</button>
    //}
    
    if(access){
        optionalButton=<LogoutButton logoutAction={logoutAction}></LogoutButton>
    }else{
        optionalButton=<LoginButton loginAction={loginAction}></LoginButton>
    }

    // Unread messages
    let addMessages = () => {
        setNmessage(nmessage+1);
    }
 
    return (
        <div>
            {/*Optional Button*/}
            {optionalButton}
            {/*n Messages unread*/}
            { nmessage > 0 && <p>You have {nmessage} new messages... </p> }
            { nmessage === 0 && <p>There are no new messages</p> }
            <button onClick={addMessages}>Add new messages</button>
        </div>
    );
}

export default Optionalrender;
